				->query(
						"UPDATE `gcms_ticketprice` SET
						 `id` =  %i 
                         [ , `id_ticket` = %I ] 
                         [ , `name` = %N ] 
                         [ , `price` = %N ] 
                         [ , `bookingfee` = %N ] 
                        
