	}

	public function insert($arr)
	{
		$query=$GLOBALS['GCMS_SAFESQL']
				->query(
						"INSERT INTO `gcms_type` (
				         `id`  , 
                         `name`  , 
                         `value` 
						) VALUES(
						 NULL  , 
                         %n  , 
                         %n 
						)", array($arr['name'] , $arr['value']));
		return $GLOBALS['GCMS_DB']->query($query);
	}
    
    public function update($arr)
	{
		$query=$GLOBALS['GCMS_SAFESQL']
				->query(
						"UPDATE `gcms_type` SET
						 `id` =  %i 
                         [ , `name` = %N ] 
                         [ , `value` = %N ] 
                        
						WHERE `id` =  %i",
						array($arr['id'] , $arr['name'] , $arr['value'], $arr['id']));
		return $GLOBALS['GCMS_DB']->query($query);
	}

    public function get($arr, $list = false, $order = "" , $limit = "")
	{
		$query=$GLOBALS['GCMS_SAFESQL']
				->query(
						"SELECT * FROM `gcms_type` WHERE TRUE
						 [ AND `id` = %I ] 
                         [ AND `name` = %N ]
                         [ AND `value` = %N ]
                        
						[ ORDER BY `%S` ] [ %S ] 
                        [ LIMIT %I ][ , %I ]
						",
						array($arr['id'] , $arr['name'] , $arr['value'], $order['by'], $order['sort'] ,$limit['start'],$limit['end']));
		if($list)
			return $GLOBALS['GCMS_DB']->get_results($query);
		else
			return $GLOBALS['GCMS_DB']->get_row($query);
	}
    
    public function get_count($arr)
	{
		$query=$GLOBALS['GCMS_SAFESQL']
				->query(
						"SELECT COUNT(*) as Num FROM `gcms_type` WHERE TRUE
					    [ AND `id` = %I ] 
                         [ AND `name` = %N ]
                         [ AND `value` = %N ]
                        
						",
						array($arr['id'] , $arr['name'] , $arr['value']));
		$result=$GLOBALS['GCMS_DB']->get_results($query);
		return $result[0]->Num;
	}

	public function del($id)
	{
		$query=$GLOBALS['GCMS_SAFESQL']
				->query("DELETE FROM `gcms_type`
						WHERE `id` =  %i", array($id));
		return $GLOBALS['GCMS_DB']->query($query);
